package variable;

public class Variable2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//자료형: 변수를 선언할 떄, 같이 쓰는 데이터의 종류를 의미
		//모든 변수에는 자료형이 존재하며, 자료형에 따라 저장할 수 있는 값의 형태와 크기가 정해짐
		//정수형: byte, short, int, long
		//실수형: float, double
		//문자형: char, string
		//논리형: boolean
		
		//char형을 사용할 때는 저장하려는 데이터에 작은 따옴표(')를 사용합니다
		
		char apple= 'A';
		System.out.println(apple);
		
		String hi = "안녕";
		String niceToMeetYou ="반가워";
		System.out.println(hi);
		System.out.println(niceToMeetYou);
		
	}

}
